// Raw output from AI agent
let raw = $json.output || "{}";

// Extract JSON block between ```json and ```
let match = raw.match(/```json([\s\S]*?)```/);
if (match) {
  raw = match[1].trim();
}

// Try parsing
let parsed = {};
try {
  parsed = JSON.parse(raw);
} catch (e) {
  parsed = {};
}

// Extract values
const topic = parsed.topic ?? "";
const region = parsed.region ?? "";
let timeframe = parsed.timeframe ?? "";

// Convert timeframe into Google News "when:" filter
let whenFilter = "";
if (timeframe) {
  const date = new Date(timeframe);
  if (!isNaN(date.getTime())) {
    const now = new Date();
    const diffDays = Math.floor((now - date) / (1000 * 60 * 60 * 24));
    if (diffDays <= 1) {
      whenFilter = "when:1d";
    } else if (diffDays <= 7) {
      whenFilter = "when:7d";
    } else if (diffDays <= 30) {
      whenFilter = "when:1m";
    }
  }
}

// Build query
const parts = [topic, region, whenFilter].filter(p => p && p.trim() !== "");
const query = encodeURIComponent(parts.join(" "));

// Return RSS URL
return [
  {
    json: {
      url: `http://news.google.com/rss/search?q=${query}&hl=en&gl=US&ceid=US:en`
    }
  }
];

