:root {
  /* Colors */
  --chat--color-primary: #2563eb; /* Tailwind blue-600 */
  --chat--color-primary-shade-50: #1d4ed8;
  --chat--color-primary-shade-100: #1e40af;
  --chat--color-secondary: #10b981; /* Tailwind emerald-500 */
  --chat--color-secondary-shade-50: #059669;
  --chat--color-white: #f3f4f6; /* changed from #ffffff to softer light gray */
  --chat--color-light: #1f2937; /* slate-800 dark background */
  --chat--color-light-shade-50: #374151; /* slate-700 */
  --chat--color-light-shade-100: #4b5563; /* slate-600 */
  --chat--color-medium: #9ca3af;
  --chat--color-dark: #f9fafb; /* text becomes near-white */
  --chat--color-disabled: #6b7280; /* dim gray */
  --chat--color-typing: #d1d5db; /* light gray typing dots */

  /* Base Layout */
  --chat--spacing: 1rem;
  --chat--border-radius: 0.75rem;
  --chat--transition-duration: 0.2s;
  --chat--font-family: 'Inter', 'Segoe UI', Roboto, Oxygen-Sans, Ubuntu, Cantarell, 'Helvetica Neue', sans-serif;

  /* Window */
  --chat--window--width: 420px;
  --chat--window--height: 640px;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--z-index: 9999;
  --chat--window--border: 1px solid var(--chat--color-light-shade-50);
  --chat--window--border-radius: 1rem;
  --chat--window--margin-bottom: var(--chat--spacing);

  /* Header */
  --chat--header--padding: 1.25rem;
  --chat--header--background: var(--chat--color-primary);
  --chat--header--color: var(--chat--color-white);
  --chat--heading--font-size: 1.5rem;
  --chat--subtitle--font-size: 0.9rem;
  --chat--subtitle--line-height: 1.6;

  /* Messages */
  --chat--message--font-size: 1rem;
  --chat--message--padding: 0.75rem 1rem;
  --chat--message--border-radius: 1rem;
  --chat--message-line-height: 1.5;
  --chat--message--margin-bottom: 0.75rem;
  --chat--message--bot--background: #374151; /* slate-700 */
  --chat--message--bot--color: var(--chat--color-white);
  --chat--message--user--background: var(--chat--color-primary);
  --chat--message--user--color: var(--chat--color-white);
  --chat--message--pre--background: rgba(255, 255, 255, 0.08);
  --chat--messages-list--padding: var(--chat--spacing);

  /* Toggle Button */
  --chat--toggle--size: 56px;
  --chat--toggle--background: var(--chat--color-primary);
  --chat--toggle--hover--background: var(--chat--color-primary-shade-50);
  --chat--toggle--active--background: var(--chat--color-primary-shade-100);
  --chat--toggle--color: var(--chat--color-white);

  /* Input Area */
  --chat--textarea--height: 52px;
  --chat--textarea--max-height: 20rem;
  --chat--input--font-size: 1rem;
  --chat--input--border: 1px solid var(--chat--color-light-shade-100);
  --chat--input--border-radius: 0.75rem;
  --chat--input--padding: 0.8rem 1rem;
  --chat--input--background: #111827; /* slate-900 */
  --chat--input--text-color: var(--chat--color-white);
  --chat--input--placeholder--font-size: 0.95rem;
  --chat--input--border-active: 1px solid var(--chat--color-primary);

  /* Buttons */
  --chat--button--color: var(--chat--color-white);
  --chat--button--background: var(--chat--color-secondary);
  --chat--button--padding: 0.6rem 1.2rem;
  --chat--button--border-radius: 0.75rem;
  --chat--button--hover--background: var(--chat--color-secondary-shade-50);

